mapper.select:
select message_info.id, message_info.message,message_info.subject, message_info.user_id,  user_info.id, user_info.phone, user_info.password, user_info.avatar_url, user_info.nickname, user_info.status
from message_info left join user_info on user_id=user_info.id
where message_info.is_deleted=0 and user_id=#{id}
