mapper.select:
select worry_info.*,  user_info.id, user_info.phone, user_info.password, user_info.avatar_url, user_info.nickname, user_info.status,worry_info.create_time
from worry_info left join user_info on user_id=user_info.id left join room_info on subject=room_info.id where room_info.landlord_id=#{userId}
And worry_info.is_deleted=0 order by worry_info.create_time desc
